function countKSubsequencesWithMaxBeauty(s: string, k: number): number {
    const mod = 1e9 + 7;
    const freq = new Array(26).fill(0);
    for (let i = 0; i < s.length; i++) {
        freq[s.charCodeAt(i) - 'a'.charCodeAt(0)]++;
    }
    const freqCounts = new Array(s.length + 1).fill(0);
    for (let i = 0; i < 26; i++) {
        if (freq[i] > 0) {
            freqCounts[freq[i]]++;
        }
    }
    let total = 0, combinations = 1;
    for (let i = s.length; i > 0; i--) {
        if (freqCounts[i] === 0) continue;
        for (let j = 1; j <= Math.min(freqCounts[i], k - total); j++) {
            combinations = (combinations * (freqCounts[i] - j + 1)) % mod;
            combinations = (combinations * modInv(j, mod)) % mod;
            if (total + j === k) {
                total = (total + combinations) % mod;
                return total;
            }
        }
        total += freqCounts[i];
        if (total >= k) break;
    }
    return total;

    function modInv(a: number, mod: number): number {
        let m0 = mod, t, q;
        let x0 = 0, x1 = 1;
        if (mod === 1) return 0;
        while (a > 1) {
            q = Math.floor(a / mod);
            t = mod;
            mod = a % mod;
            a = t;
            t = x0;
            x0 = x1 - q * x0;
            x1 = t;
        }
        if (x1 < 0) x1 += m0;
        return x1;
    }
}
