function countKSubsequencesWithMaxBeauty(s: string, k: number): number {
    const mod = 1e9 + 7;
    const freq = new Map<string, number>();
    for (const char of s) {
        freq.set(char, (freq.get(char) || 0) + 1);
    }
    const beauties = Array.from(freq.values());
    beauties.sort((a, b) => b - a);

    let maxBeauty = 0;
    for (let i = 0; i < k; i++) {
        maxBeauty += beauties[i];
    }

    let ways = 1;
    let freqCounter = new Map<number, number>();
    for (const beauty of beauties) {
        freqCounter.set(beauty, (freqCounter.get(beauty) || 0) + 1);
    }

    for (let i = 0; i < k; i++) {
        const freqOfBeauty = freqCounter.get(beauties[i])!;
        ways = (ways * freqOfBeauty) % mod;
        freqCounter.set(beauties[i], freqOfBeauty - 1);
    }

    return ways;
}
