func countSubMultisets(nums []int, l int, r int) int {
    const mod = 1000000007
    maxSum := 0
    for _, num := range nums {
        maxSum += num
    }
    if maxSum < l {
        return 0
    }

    dp := make([]int, maxSum+1)
    dp[0] = 1
    for _, num := range nums {
        for j := maxSum; j >= num; j-- {
            dp[j] = (dp[j] + dp[j-num]) % mod
        }
    }

    ans := 0
    for i := l; i <= r; i++ {
        ans = (ans + dp[i]) % mod
    }

    // Adjust for the case where nums contains zeros, as they can be included in any multiset without affecting the sum
    zeroCount := 0
    for _, num := range nums {
        if num == 0 {
            zeroCount++
        }
    }
    if zeroCount > 0 {
        for i := l; i <= r; i++ {
            if i == 0 {
                // Calculate the total number of multisets that can be formed with zeroCount zeros
                // Subtract 1 because the empty set is already counted in dp[0]
                ans = (ans + mod + (pow(2, zeroCount, mod) - 1)) % mod
            }
        }
    }

    return ans
}

// pow calculates base^exponent % mod using the binary exponentiation method
func pow(base, exponent, mod int) int {
    result := 1
    for exponent > 0 {
        if exponent&1 == 1 {
            result = (result * base) % mod
        }
        base = (base * base) % mod
        exponent >>= 1
    }
    return result
}
