public class Solution {
    public int NumberOfGoodPartitions(int[] nums) {
        const int MOD = 1000000007;
        int n = nums.Length;
        Dictionary<int, int> lastPos = new Dictionary<int, int>();
        int[] dp = new int[n + 1];
        dp[0] = 1; // Base case: only 1 way to partition empty array

        for (int i = 1; i <= n; i++) {
            dp[i] = (2 * dp[i - 1]) % MOD; // Each new number can either start a new subarray or be part of the previous one
            if (lastPos.ContainsKey(nums[i - 1])) {
                int j = lastPos[nums[i - 1]];
                dp[i] = (dp[i] - dp[j - 1] + MOD) % MOD; // Subtract the invalid partitions caused by repeating a number
            }
            lastPos[nums[i - 1]] = i; // Update the last position of the current number
        }

        return dp[n];
    }
}
