#include <vector>
#include <unordered_map>
using namespace std;

class Solution {
public:
    int numberOfGoodPartitions(vector<int>& nums) {
        const int mod = 1e9 + 7;
        int n = nums.size();
        vector<int> dp(n + 1, 0);
        unordered_map<int, int> lastPosition;
        dp[0] = 1; // base case: empty array has 1 good partition

        for (int i = 1; i <= n; ++i) {
            dp[i] = (2 * dp[i - 1]) % mod; // initially double the previous count
            if (lastPosition.count(nums[i - 1])) {
                // if the current number has appeared before, subtract the partitions count
                // right before the previous appearance to avoid counting invalid partitions
                int lastPos = lastPosition[nums[i - 1]];
                dp[i] = (dp[i] - dp[lastPos - 1] + mod) % mod; // add mod to avoid negative result
            }
            lastPosition[nums[i - 1]] = i; // update last position of nums[i - 1]
        }

        return dp[n];
    }
};
