#include <vector>
#include <unordered_map>
using namespace std;

class Solution {
public:
    int numberOfGoodPartitions(vector<int>& nums) {
        const int mod = 1e9 + 7;
        int n = nums.size();
        vector<long long> dp(n + 1, 0);
        unordered_map<int, int> lastIndex;
        dp[0] = 1; // base case: empty array has 1 good partition

        for (int i = 1; i <= n; ++i) {
            dp[i] = dp[i - 1]; // start with the number of partitions ending at the previous element
            if (lastIndex.find(nums[i - 1]) != lastIndex.end()) {
                if (lastIndex[nums[i - 1]] > 0) // ensure there's a previous occurrence
                    dp[i] = (dp[i] - dp[lastIndex[nums[i - 1]] - 1] + mod) % mod;
            } else {
                dp[i] = (2 * dp[i - 1]) % mod; // if it's the first occurrence, double the possibilities
            }
            lastIndex[nums[i - 1]] = i; // update the last index of this number
        }

        return (int)dp[n];
    }
};
