#include <vector>
#include <unordered_map>
using namespace std;

class Solution {
public:
    int numberOfGoodPartitions(vector<int>& nums) {
        const int MOD = 1e9 + 7;
        vector<int> dp(nums.size() + 1);
        unordered_map<int, int> last;
        dp[0] = 1; // Base case: one way to partition an empty array

        for (int i = 1; i <= nums.size(); ++i) {
            dp[i] = (2 * dp[i - 1]) % MOD; // Each number can be part of a new subarray or join the previous one
            if (last.find(nums[i - 1]) != last.end()) {
                // Subtract the patterns counted twice
                dp[i] = (dp[i] - dp[last[nums[i - 1]]] + MOD) % MOD;
            }
            last[nums[i - 1]] = i - 1; // Update the last occurrence of nums[i-1]
        }

        return dp[nums.size()];
    }
};
