class Solution {
    public int numberOfGoodPartitions(int[] nums) {
        final int MOD = 1000000007;
        int[] dp = new int[nums.length + 1];
        dp[0] = 1; // Base case: no numbers means only one way to partition
        int[] lastPosition = new int[1000001];
        for (int i = 0; i < nums.length; i++) {
            dp[i + 1] = (2 * dp[i]) % MOD; // Each element can either start a new subarray or be part of the previous
            if (lastPosition[nums[i]] != 0) {
                dp[i + 1] = (dp[i + 1] - dp[lastPosition[nums[i]] - 1] + MOD) % MOD; // Remove invalid partitions
            }
            lastPosition[nums[i]] = i + 1; // Update the last position of the current element
        }
        return (dp[nums.length] - 1 + MOD) % MOD; // Subtract the partition that includes the whole array
    }
}
