class Solution {
    public int numberOfGoodPartitions(int[] nums) {
        final int MOD = 1000000007;
        int n = nums.length;
        int[] dp = new int[n + 1];
        int[] last = new int[1000001];
        dp[0] = 1; // There's 1 way to partition an empty array

        for (int i = 1; i <= n; i++) {
            dp[i] = (2 * dp[i - 1]) % MOD; // Every new element can either form a new subarray or join the previous ones
            if (last[nums[i - 1]] != 0) { // If the element has been seen before
                // Subtract the partitions that are not allowed (those that would include this number in two subarrays)
                dp[i] = (dp[i] - dp[last[nums[i - 1]] - 1] + MOD) % MOD;
            }
            last[nums[i - 1]] = i; // Update the last seen position of this number
        }

        // Since we start counting from an empty partition, subtract 1 to exclude the empty set
        return (dp[n] - 1 + MOD) % MOD;
    }
}
