class Solution {
    fun numberOfGoodPartitions(nums: IntArray): Int {
        val mod = 1000000007
        val lastIndex = mutableMapOf<Int, Int>()
        val dp = IntArray(nums.size + 1) { 0 }
        dp[0] = 1  // Base case: empty array has 1 good partition

        for (i in nums.indices) {
            dp[i + 1] = dp[i] * 2 % mod  // Every element can either start a new subarray or join the previous one
            if (nums[i] in lastIndex) {
                // If the number was seen before, we subtract the partitions that include this number in two different subarrays
                dp[i + 1] = (dp[i + 1] - dp[lastIndex[nums[i]]!!] + mod) % mod
            }
            lastIndex[nums[i]] = i  // Update the last index where this number was seen
        }

        return dp[nums.size]
    }
}
