class Solution {

    /**
     * @param Integer[] $nums
     * @return Integer
     */
    function numberOfGoodPartitions($nums) {
        $mod = 1000000007;
        $dp = [1]; // dp[0] = 1, base case
        $lastIndex = [];
        $n = count($nums);

        for ($i = 0; $i < $n; $i++) {
            $num = $nums[$i];
            $dp[$i + 1] = $dp[$i] * 2 % $mod; // Initially double the previous value
            if (array_key_exists($num, $lastIndex)) {
                // Subtract the patterns counted before the last occurrence of the same number
                $dp[$i + 1] -= $dp[$lastIndex[$num]];
            }
            $dp[$i + 1] = ($dp[$i + 1] + $mod) % $mod; // Ensure positive modulo
            $lastIndex[$num] = $i; // Update last index of the current number
        }

        return $dp[$n];
    }
}
