#include <vector>
#include <algorithm>

class Solution {
public:
    long long incremovableSubarrayCount(std::vector<int>& nums) {
        int n = nums.size();
        std::vector<int> left(n), right(n);
        std::vector<std::pair<int, int>> stack;

        // Compute the next smaller element on the left
        for (int i = 0; i < n; ++i) {
            while (!stack.empty() && stack.back().first >= nums[i]) stack.pop_back();
            left[i] = stack.empty() ? -1 : stack.back().second;
            stack.emplace_back(nums[i], i);
        }

        stack.clear();

        // Compute the next smaller element on the right
        for (int i = n - 1; i >= 0; --i) {
            while (!stack.empty() && stack.back().first > nums[i]) stack.pop_back();
            right[i] = stack.empty() ? n : stack.back().second;
            stack.emplace_back(nums[i], i);
        }

        long long ans = 0;
        for (int i = 0; i < n; ++i) {
            ans += (long long)(i - left[i]) * (right[i] - i);
        }

        return ans;
    }
};
