#include <vector>
#include <algorithm>

class Solution {
public:
    long long incremovableSubarrayCount(std::vector<int>& nums) {
        int n = nums.size();
        std::vector<int> dp(n, 1);
        long long result = 0;

        for (int i = 1; i < n; ++i) {
            if (nums[i] > nums[i - 1]) {
                dp[i] = dp[i - 1] + 1;
            }
            result += dp[i];
        }

        // Add the single-element subarrays
        result += n;

        // Process for decreasing sequences
        for (int i = 0; i < n; ++i) {
            int j = i;
            while (j < n - 1 && nums[j] > nums[j + 1]) {
                ++j;
            }
            if (j != i) {
                result += (j - i) * (j - i + 1) / 2;
                i = j;
            }
        }

        return result;
    }
};
