#include <vector>

class Solution {
public:
    long long incremovableSubarrayCount(std::vector<int>& nums) {
        long long count = 0;
        int n = nums.size();
        std::vector<int> dp(n, 1); // dp[i] represents the length of longest increasing subsequence ending at i
        std::vector<int> lis(n, 1); // lis[i] represents the length of longest increasing subsequence starting from i

        // Calculate dp (LIS ending at each index)
        for (int i = 1; i < n; ++i) {
            if (nums[i] > nums[i - 1]) dp[i] += dp[i - 1];
        }

        // Calculate lis (LIS starting from each index)
        for (int i = n - 2; i >= 0; --i) {
            if (nums[i] < nums[i + 1]) lis[i] += lis[i + 1];
        }

        // Count total incremovable subarrays
        for (int i = 0; i < n; ++i) {
            count += dp[i]; // Subarrays ending at i
            if (i < n - 1 && nums[i] >= nums[i + 1]) {
                // Count subarrays that can be formed by excluding elements after i
                // which are part of a decreasing sequence starting from i
                count += lis[i + 1] - 1;
            }
        }

        return count;
    }
};
