long long incremovableSubarrayCount(int* nums, int numsSize) {
    long long count = 0;
    int left = 0, right = 0;

    while (left < numsSize) {
        right = left;
        while (right + 1 < numsSize && nums[right] < nums[right + 1]) {
            right++;
        }
        long long length = right - left + 1;
        count += (length * (length + 1)) / 2;
        left = right + 1;
        if (left < numsSize && nums[left - 1] >= nums[left]) {
            int decreaseCount = 2;
            while (left - decreaseCount >= 0 && nums[left - decreaseCount] >= nums[left - decreaseCount + 1]) {
                count -= decreaseCount;
                decreaseCount++;
            }
        }
    }
    return count;
}
