func incremovableSubarrayCount(nums []int) int64 {
    n := len(nums)
    totalSubarrays := int64(n) * (int64(n) + 1) / 2 // Total number of subarrays
    var stack []int // To keep track of the start of non-increasing sequences
    var badSubarrays int64 // To count subarrays that cannot be removed to make the array strictly increasing

    for i, num := range nums {
        for len(stack) > 0 && nums[stack[len(stack)-1]] < num {
            // When we find a number that is larger than the previous ones,
            // it means the sequence can end with this number to be strictly increasing.
            // We pop all elements that are smaller than the current number.
            lastIndex := stack[len(stack)-1]
            stack = stack[:len(stack)-1]
            // Calculate the width of the sequence that ends at lastIndex
            leftIndex := -1
            if len(stack) > 0 {
                leftIndex = stack[len(stack)-1]
            }
            // Count subarrays where lastIndex is the maximum element and cannot be removed
            // to make the sequence strictly increasing
            badSubarrays += int64(lastIndex-leftIndex) * int64(i-lastIndex)
        }
        stack = append(stack, i)
    }

    // Count remaining sequences that are non-increasing towards the end
    for len(stack) > 0 {
        lastIndex := stack[len(stack)-1]
        stack = stack[:len(stack)-1]
        leftIndex := -1
        if len(stack) > 0 {
            leftIndex = stack[len(stack)-1]
        }
        badSubarrays += int64(lastIndex-leftIndex) * int64(n-lastIndex)
    }

    // Subtract the bad subarrays from the total to get the incremovable subarrays
    return totalSubarrays - badSubarrays
}
