func incremovableSubarrayCount(nums []int) int64 {
    var result int64
    stack := []int{-1} // Initialize stack with -1 to handle edge case for the first element
    nums = append(nums, 1<<31-1) // Append a large number at the end to flush the stack

    for i, num := range nums {
        for len(stack) > 1 && num >= nums[stack[len(stack)-1]] {
            // Pop the middle element of a non-strictly increasing triplet
            mid := stack[len(stack)-1]
            stack = stack[:len(stack)-1]

            // Calculate the number of subarrays ending with mid as the smallest element
            // that can be removed to make the remaining array strictly increasing
            left := stack[len(stack)-1]
            right := i

            // Add the number of such subarrays to the result
            result += int64(mid-left) * int64(right-mid)
        }
        stack = append(stack, i)
    }

    return result
}
