/**
 * @param {number[]} nums
 * @return {number}
 */
var incremovableSubarrayCount = function(nums) {
    let n = nums.length, count = 0;
    let left = new Array(n).fill(0), right = new Array(n).fill(0);

    // Compute the length of non-decreasing sequence ending at each position
    left[0] = 1;
    for (let i = 1; i < n; ++i) {
        left[i] = nums[i] > nums[i - 1] ? left[i - 1] + 1 : 1;
    }

    // Compute the length of non-decreasing sequence starting at each position
    right[n - 1] = 1;
    for (let i = n - 2; i >= 0; --i) {
        right[i] = nums[i] < nums[i + 1] ? right[i + 1] + 1 : 1;
    }

    // Count total incremovable subarrays
    for (let i = 0; i < n; ++i) {
        if (i > 0 && i < n - 1 && nums[i - 1] < nums[i + 1]) {
            count += left[i - 1] * right[i + 1];
        } else {
            count += left[i] + right[i] - 1;
        }
    }

    // Remove duplicates (each element is counted twice except the ones that can't form an incremovable subarray)
    count -= n;

    return count;
};
