from typing import List

class Solution:
    def incremovableSubarrayCount(self, nums: List[int]) -> int:
        def is_incremovable(left, right):
            return left == 0 or right == len(nums) or nums[left - 1] < nums[right]

        n = len(nums)
        dp = [0] * (n + 1)  # dp[i] stores the count of strictly increasing sequences ending at i
        total = 0

        for i in range(n):
            dp[i] = 1 + (dp[i - 1] if i > 0 and nums[i - 1] < nums[i] else 0)
            total += dp[i]

        # Count the subarrays where removal does not result in strictly increasing order
        for i in range(1, n):
            if nums[i - 1] >= nums[i]:
                left = i - 1
                while left > 0 and nums[left - 1] < nums[i]:
                    left -= 1
                right = i + 1
                while right < n and nums[right - 1] < nums[right]:
                    right += 1
                total += (i - left) * (right - i)

        return total
