class Solution {
    func incremovableSubarrayCount(_ nums: [Int]) -> Int {
        var result = 0
        let n = nums.count
        var prefix = Array(repeating: 0, count: n)
        var suffix = Array(repeating: 0, count: n)

        prefix[0] = 1
        for i in 1..<n {
            if nums[i] > nums[i-1] {
                prefix[i] = prefix[i-1] + 1
            } else {
                prefix[i] = 1
            }
        }

        suffix[n-1] = 1
        for i in stride(from: n-2, through: 0, by: -1) {
            if nums[i] < nums[i+1] {
                suffix[i] = suffix[i+1] + 1
            } else {
                suffix[i] = 1
            }
        }

        for i in 0..<n {
            if i > 0 && i < n-1 && nums[i-1] < nums[i+1] {
                result = max(result, prefix[i-1] + suffix[i+1])
            }
            result = max(result, max(prefix[i], suffix[i]))
        }

        var totalIncremovableSubarrays = 0
        for i in 0..<n {
            totalIncremovableSubarrays += (prefix[i] * (prefix[i] + 1)) / 2
            if i > 0 && nums[i] > nums[i-1] {
                totalIncremovableSubarrays -= (suffix[i] * (suffix[i] - 1)) / 2
            }
        }

        return totalIncremovableSubarrays - result
    }
}
