public class Solution {
    public int NumberOfSequence(int n, int[] sick) {
        const int MOD = 1000000007;
        long result = 1;
        for (int i = 1; i < sick.Length; i++) {
            int gap = sick[i] - sick[i - 1] - 1;
            result = result * Pow(2, gap - 1, MOD) % MOD;
        }
        if (sick[0] > 0) result = result * Pow(2, sick[0] - 1, MOD) % MOD;
        if (sick[sick.Length - 1] < n - 1) result = result * Pow(2, n - sick[sick.Length - 1] - 2, MOD) % MOD;
        return (int)result;
    }

    private long Pow(long baseVal, int exponent, int mod) {
        long result = 1;
        while (exponent > 0) {
            if (exponent % 2 == 1) result = result * baseVal % mod;
            baseVal = baseVal * baseVal % mod;
            exponent /= 2;
        }
        return result;
    }
}
