#include <vector>
using namespace std;

class Solution {
public:
    int numberOfSequence(int n, vector<int>& sick) {
        const int MOD = 1e9 + 7;
        vector<int> segments;
        int prev = -1;

        for (int i : sick) {
            if (prev != -1) segments.push_back(i - prev - 1);
            prev = i;
        }

        if (sick[0] > 0) segments.push_back(sick[0]);
        if (sick.back() < n - 1) segments.push_back(n - 1 - sick.back());

        long long res = 1;
        for (int seg : segments) {
            res = (res * countWays(seg)) % MOD;
        }

        return res;
    }

private:
    long long countWays(int len) {
        if (len <= 1) return 1;
        long long first = 1, second = 1;
        for (int i = 2; i <= len; i++) {
            long long temp = second;
            second = (second + first) % (int)(1e9 + 7);
            first = temp;
        }
        return second;
    }
};
