#include <stdio.h>
#include <stdlib.h>

#define MOD 1000000007

long long factorial(int n) {
    long long fact = 1;
    for (int i = 2; i <= n; ++i) {
        fact = (fact * i) % MOD;
    }
    return fact;
}

long long power(long long x, int y, int p) {
    long long res = 1;
    x = x % p;
    while (y > 0) {
        if (y & 1)
            res = (res * x) % p;
        y = y >> 1;
        x = (x * x) % p;
    }
    return res;
}

long long modInverse(long long n, int p) {
    return power(n, p - 2, p);
}

long long nCrModPFermat(int n, int r, int p) {
    if (r == 0)
        return 1;
    long long fac[n + 1];
    fac[0] = 1;
    for (int i = 1; i <= n; i++)
        fac[i] = fac[i - 1] * i % p;
    return (fac[n] * modInverse(fac[r], p) % p * modInverse(fac[n - r], p) % p) % p;
}

int numberOfSequence(int n, int* sick, int sickSize) {
    int totalGaps = 0, totalChoices = 1;
    for (int i = 0; i <= sickSize; ++i) {
        int gap = (i == 0 ? sick[0] : (i == sickSize ? n - 1 - sick[sickSize - 1] : sick[i] - sick[i - 1] - 1));
        if (gap > 0) {
            totalGaps += gap;
            totalChoices = (int)((long long)totalChoices * factorial(gap) % MOD);
            if (i > 0 && i < sickSize) {
                totalChoices = (int)((long long)totalChoices * modInverse(2, MOD) % MOD);
            }
        }
    }
    return (int)((long long)totalChoices * nCrModPFermat(totalGaps, n - sickSize, MOD) % MOD);
}
