class Solution {
  int numberOfSequence(int n, List<int> sick) {
    const int mod = 1000000007;
    List<int> segments = [];
    int total = 1, last = -1;
    for (int i = 0; i < sick.length; i++) {
      if (i == 0) {
        segments.add(sick[i]);
      } else {
        segments.add(sick[i] - sick[i - 1] - 1);
      }
      if (i == sick.length - 1) {
        segments.add(n - 1 - sick[i]);
      }
    }
    for (int i = 0; i < segments.length; i++) {
      if (segments[i] <= 0) continue;
      if (i > 0 && i < segments.length - 1) {
        total = (total * calculateWays(segments[i] + 1, mod)) % mod;
      } else {
        total = (total * calculateWays(segments[i], mod)) % mod;
      }
    }
    return total;
  }

  int calculateWays(int segmentLength, int mod) {
    int ways = 1;
    for (int i = 1; i <= segmentLength - 1; i++) {
      ways = (ways * 2) % mod;
    }
    return ways;
  }
}
