func numberOfSequence(n int, sick []int) int {
    const mod = 1000000007

    // Helper function to calculate factorial modulo mod
    factorial := func(n int) int {
        f := 1
        for i := 2; i <= n; i++ {
            f = (f * i) % mod
        }
        return f
    }

    totalWays := 1
    // Calculating the gaps between sick children and at the ends
    gaps := make([]int, 0)

    // Gap at the beginning
    if sick[0] > 0 {
        gaps = append(gaps, sick[0]-1)
    }

    // Gaps between sick children
    for i := 0; i < len(sick)-1; i++ {
        gap := sick[i+1] - sick[i] - 1
        if gap > 0 {
            gaps = append(gaps, gap)
        }
    }

    // Gap at the end
    if sick[len(sick)-1] < n-1 {
        gaps = append(gaps, n-1-sick[len(sick)-1])
    }

    // Calculating ways for each gap
    for _, gap := range gaps {
        totalWays = (totalWays * factorial(gap)) % mod
    }

    return totalWays
}
