class Solution {
    public int numberOfSequence(int n, int[] sick) {
        final int MOD = 1000000007;
        long totalWays = 1;
        for (int i = 1; i < sick.length; i++) {
            int gap = sick[i] - sick[i - 1] - 1;
            totalWays = (totalWays * calculateWays(gap)) % MOD;
        }
        // Check for gaps at the beginning and end of the queue
        if (sick[0] > 0) totalWays = (totalWays * calculateWays(sick[0])) % MOD;
        if (sick[sick.length - 1] < n - 1) totalWays = (totalWays * calculateWays(n - 1 - sick[sick.length - 1])) % MOD;
        return (int) totalWays;
    }

    private long calculateWays(int gap) {
        // As each gap can only be infected from one side, there's only one way to infect the gap
        return gap == 0 ? 1 : gap;
    }
}
