/**
 * @param {number} n
 * @param {number[]} sick
 * @return {number}
 */
var numberOfSequence = function(n, sick) {
    const mod = 1000000007;

    function factorial(x) {
        let result = 1;
        for (let i = 2; i <= x; i++) {
            result = (result * i) % mod;
        }
        return result;
    }

    function catalanNumber(x) {
        if (x <= 1) return 1;
        let c = factorial(2 * x);
        let temp = (factorial(x + 1) * factorial(x)) % mod;
        c = c * pow(temp, mod - 2) % mod;
        return c;
    }

    function pow(x, y) {
        let result = 1;
        x = x % mod;
        while (y > 0) {
            if (y & 1) result = (result * x) % mod;
            y = y >> 1;
            x = (x * x) % mod;
        }
        return result;
    }

    let totalWays = 1;
    for (let i = 0; i < sick.length - 1; i++) {
        let gap = sick[i + 1] - sick[i] - 1;
        if (gap > 0) {
            totalWays = (totalWays * catalanNumber(gap)) % mod;
        }
    }

    for (let i = 1; i < sick.length; i++) {
        let gap = sick[i] - sick[i - 1] - 1;
        totalWays = (totalWays * factorial(gap)) % mod;
    }

    let leftGap = sick[0], rightGap = n - 1 - sick[sick.length - 1];
    if (leftGap > 0) totalWays = (totalWays * factorial(leftGap)) % mod;
    if (rightGap > 0) totalWays = (totalWays * factorial(rightGap)) % mod;

    return totalWays;
};
