/**
 * @param {number} n
 * @param {number[]} sick
 * @return {number}
 */
var numberOfSequence = function(n, sick) {
    const MOD = 1000000007;
    let dp = Array(n).fill(0);
    dp[0] = 1;
    for (let i = 1; i < n; i++) dp[i] = (dp[i - 1] * i) % MOD;

    function modInverse(a, m = MOD) {
        let m0 = m, t, q;
        let x0 = 0, x1 = 1;
        if (m === 1) return 0;
        while (a > 1) {
            q = Math.floor(a / m);
            t = m;
            m = a % m;
            a = t;
            t = x0;
            x0 = x1 - q * x0;
            x1 = t;
        }
        if (x1 < 0) x1 += m0;
        return x1;
    }

    function nCr(n, r) {
        if (n < r) return 0;
        if (n === r || r === 0) return 1;
        return (dp[n] * modInverse(dp[r]) % MOD) * modInverse(dp[n - r]) % MOD;
    }

    let ans = 1, prev = -1;
    for (let i = 0; i < sick.length; i++) {
        if (prev !== -1) {
            let len = sick[i] - prev - 1;
            ans = (ans * nCr(len + 1, 1)) % MOD;
        }
        prev = sick[i];
    }
    for (let i = 1; i < sick.length; i++) {
        let len = sick[i] - sick[i - 1] - 1;
        ans = (ans * dp[len]) % MOD;
    }
    let left = sick[0], right = n - 1 - sick[sick.length - 1];
    ans = (ans * dp[left]) % MOD;
    ans = (ans * dp[right]) % MOD;
    return ans;
};
