class Solution {
    fun numberOfSequence(n: Int, sick: IntArray): Int {
        val mod = 1000000007
        var totalWays = 1L

        // Add virtual sick children at the ends
        val extendedSick = IntArray(sick.size + 2)
        for (i in sick.indices) {
            extendedSick[i + 1] = sick[i]
        }
        extendedSick[0] = -1 // Virtual sick child before the first child
        extendedSick[extendedSick.lastIndex] = n // Virtual sick child after the last child

        for (i in 1 until extendedSick.size) {
            val gap = extendedSick[i] - extendedSick[i - 1] - 1
            if (gap > 0) {
                totalWays *= factorial(gap)
                totalWays %= mod
            }
        }
        return totalWays.toInt()
    }

    private fun factorial(n: Int): Long {
        val mod = 1000000007
        var result = 1L
        for (i in 2..n) {
            result *= i
            result %= mod
        }
        return result
    }
}
