class Solution {
    fun numberOfSequence(n: Int, sick: IntArray): Int {
        val mod = 1000000007
        var ways = 1L
        val gaps = mutableListOf<Int>()

        for (i in 1 until sick.size) {
            val gap = sick[i] - sick[i - 1] - 1
            if (gap > 0) gaps.add(gap)
        }

        if (sick.first() > 0) gaps.add(sick.first())
        if (sick.last() < n - 1) gaps.add(n - 1 - sick.last())

        for (gap in gaps) {
            ways *= catalanNumber(gap)
            ways %= mod
        }

        return ways.toInt()
    }

    private fun catalanNumber(n: Int): Long {
        val mod = 1000000007
        if (n == 0) return 1
        var c = 1L
        for (i in 0 until n) {
            c *= 2L * (2 * i + 1)
            c /= i + 2
            c %= mod
        }
        return c
    }
}
