class Solution(object):
    def numberOfSequence(self, n, sick):
        """
        :type n: int
        :type sick: List[int]
        :rtype: int
        """
        MOD = 10**9 + 7
        def factorial(x):
            ans = 1
            for i in range(1, x + 1):
                ans = (ans * i) % MOD
            return ans

        def countWays(gaps):
            ways = 1
            for gap in gaps:
                ways = (ways * factorial(gap)) % MOD
            return ways

        gaps = []
        prev = -1
        for i in sick:
            if prev != -1:
                gap = i - prev - 1
                if gap > 0:
                    gaps.append(gap)
            prev = i
        # Adding the gaps at the start and end
        if sick[0] > 0:
            gaps.append(sick[0])
        if sick[-1] < n - 1:
            gaps.append(n - 1 - sick[-1])

        return countWays(gaps)
