from typing import List

class Solution:
    def numberOfSequence(self, n: int, sick: List[int]) -> int:
        MOD = 10**9 + 7
        def factorial(x):
            return 1 if x == 0 else (x * factorial(x - 1)) % MOD

        def countWays(length):
            if length <= 1:
                return 1
            return factorial(length - 1)

        total_ways = 1
        prev = -1
        for s in sick:
            if prev != -1:
                gap_length = s - prev - 1
                total_ways *= countWays(gap_length)
                total_ways %= MOD
            prev = s

        # Calculate for the gap before the first sick child and after the last sick child
        total_ways *= countWays(sick[0]) * countWays(n - 1 - sick[-1])
        total_ways %= MOD

        return total_ways
