from typing import List

class Solution:
    def numberOfSequence(self, n: int, sick: List[int]) -> int:
        MOD = 10**9 + 7

        def dp(x):
            # Base case: when there's only one child to infect, there's only one way to do it.
            if x <= 1:
                return 1
            # If we already calculated the number of ways for x children, return it.
            if x in memo:
                return memo[x]
            # The recurrence relation: dp(x) = x * dp(x - 1)
            memo[x] = x * dp(x - 1) % MOD
            return memo[x]

        memo = {}
        total_ways = 1
        prev = 0
        for i in range(len(sick)):
            # Distance between two consecutive sick children or the first/last sick child and the end of the queue.
            if i == 0:
                gap = sick[i] - prev
            else:
                gap = sick[i] - sick[i-1] - 1
            if i == len(sick) - 1 and sick[i] != n - 1:
                gap += n - sick[i] - 1
            # Calculate the total number of ways for the current gap and multiply it with the total ways.
            total_ways *= dp(gap)
            total_ways %= MOD
            prev = sick[i] + 1

        return total_ways
