function numberOfSequence(n: number, sick: number[]): number {
    const mod = 1e9 + 7;

    function factorial(x: number): number {
        let result = 1;
        for (let i = 2; i <= x; i++) {
            result = (result * i) % mod;
        }
        return result;
    }

    function modInverse(a: number, m: number): number {
        let m0 = m;
        let y = 0, x = 1;

        if (m == 1)
            return 0;

        while (a > 1) {
            let q = Math.floor(a / m);
            let t = m;

            m = a % m;
            a = t;
            t = y;

            y = x - q * y;
            x = t;
        }

        if (x < 0)
            x += m0;

        return x;
    }

    function nCr(n: number, r: number): number {
        if (r === 0 || n === r) return 1;
        if (r > n) return 0;
        return (factorial(n) * modInverse((factorial(r) * factorial(n - r)) % mod, mod)) % mod;
    }

    let totalWays = 1;
    let freeSpaces = n - sick.length;

    for (let i = 0; i < sick.length - 1; i++) {
        let gap = sick[i + 1] - sick[i] - 1;
        if (gap > 0) {
            totalWays = (totalWays * nCr(freeSpaces - 1, gap - 1)) % mod;
            freeSpaces -= gap;
        }
    }

    return totalWays;
}
