function numberOfSequence(n: number, sick: number[]): number {
    const mod = 1000000007;
    let gaps: number[] = [];
    for (let i = 0; i < sick.length - 1; i++) {
        // Calculate the gap between consecutive sick children, subtract 1 because we don't count the sick children themselves
        gaps.push(sick[i + 1] - sick[i] - 1);
    }
    // Add the gaps before the first sick child and after the last one
    if (sick[0] > 0) gaps.push(sick[0]);
    if (sick[sick.length - 1] < n - 1) gaps.push(n - 1 - sick[sick.length - 1]);

    let totalWays = 1;
    for (let gap of gaps) {
        // For each gap, there are 2^(gap - 1) ways to infect the children in this gap, if gap > 0
        if (gap > 1) totalWays = (totalWays * Math.pow(2, gap - 1)) % mod;
    }

    return totalWays;
}
