#include <vector>
#include <limits>

using namespace std;

class Solution {
public:
    long long minimumCost(vector<int>& nums, int k, int dist) {
        int n = nums.size();
        vector<long long> dp(n + 1, LLONG_MAX);
        dp[0] = 0;

        for (int i = 1; i <= k; i++) {
            vector<long long> newDp(n + 1, LLONG_MAX);
            for (int j = i; j <= n; j++) {
                for (int l = max(i - 1, j - dist - 1); l < j; l++) {
                    if (dp[l] != LLONG_MAX) {
                        newDp[j] = min(newDp[j], dp[l] + nums[j - 1]);
                    }
                }
            }
            dp = move(newDp);
        }

        long long ans = LLONG_MAX;
        for (int i = k; i <= n; i++) {
            ans = min(ans, dp[i]);
        }
        return ans;
    }
};
