#include <vector>
#include <limits>

using namespace std;

class Solution {
public:
    long long minimumCost(vector<int>& nums, int k, int dist) {
        int n = nums.size();
        vector<vector<long long>> dp(k + 1, vector<long long>(n, LLONG_MAX));

        for (int i = 0; i < n; ++i) {
            dp[1][i] = nums[i];
        }

        for (int i = 2; i <= k; ++i) {
            for (int j = i - 1; j < n; ++j) {
                for (int m = j - 1; m >= i - 2 && j - m <= dist; --m) {
                    if (dp[i-1][m] != LLONG_MAX) {
                        dp[i][j] = min(dp[i][j], dp[i-1][m] + nums[j]);
                    }
                }
            }
        }

        long long ans = LLONG_MAX;
        for (int i = k - 1; i < n; ++i) {
            ans = min(ans, dp[k][i]);
        }
        return ans;
    }
};
