#include <limits.h>

long long minimumCost(int* nums, int numsSize, int k, int dist) {
    long long dp[k][numsSize];
    for (int i = 0; i < k; ++i) {
        for (int j = 0; j < numsSize; ++j) {
            dp[i][j] = LLONG_MAX;
        }
    }

    for (int i = 0; i < numsSize; ++i) {
        dp[0][i] = nums[0]; // First subarray starts with the first element
    }

    for (int i = 1; i < k; ++i) { // For each additional subarray
        for (int j = i; j < numsSize; ++j) { // Start from 'i' because we need at least 'i' elements before j to form 'i' subarrays
            long long minCost = LLONG_MAX;
            for (int m = j - 1; m >= 0 && (j - m) <= dist + 1; --m) {
                if (dp[i - 1][m] != LLONG_MAX) {
                    minCost = fmin(minCost, dp[i - 1][m] + nums[j]);
                }
            }
            dp[i][j] = minCost;
        }
    }

    long long result = LLONG_MAX;
    for (int i = k - 1; i < numsSize; ++i) {
        result = fmin(result, dp[k - 1][i]);
    }

    return result;
}
