func minimumCost(nums []int, k int, dist int) int64 {
    n := len(nums)
    dp := make([][]int64, k+1)
    for i := range dp {
        dp[i] = make([]int64, n+1)
        for j := range dp[i] {
            if i == 0 {
                dp[i][j] = 0
            } else {
                dp[i][j] = 1<<60 // Use a smaller max value to avoid serialization error
            }
        }
    }

    for i := 1; i <= k; i++ {
        for j := i - 1; j < n; j++ {
            if i == 1 {
                dp[i][j+1] = int64(nums[0])
            } else {
                for p := max(0, j-dist); p < j; p++ {
                    dp[i][j+1] = min(dp[i][j+1], dp[i-1][p+1]+int64(nums[j]))
                }
            }
        }
    }

    result := int64(1<<60)
    for i := n - 1; i >= n-dist-1 && i >= 0; i-- {
        result = min(result, dp[k][i+1])
    }
    return result
}

func min(a, b int64) int64 {
    if a < b {
        return a
    }
    return b
}

func max(a, b int) int {
    if a > b {
        return a
    }
    return b
}
