class Solution {
    public long minimumCost(int[] nums, int k, int dist) {
        final long INF = Long.MAX_VALUE / 2; // To avoid overflow
        int n = nums.length;
        long[] dp = new long[n];
        long[] next = new long[n];

        for (int i = 0; i < n; ++i) {
            dp[i] = nums[i]; // Base case for the first subarray
        }

        for (int subarray = 2; subarray <= k; ++subarray) {
            System.arraycopy(dp, 0, next, 0, n); // Copy current dp to next

            for (int i = n - 1; i >= 0; --i) {
                dp[i] = INF; // Reset dp for new subarray calculation
                for (int j = i + 1; j < n && j - i <= dist + 1; ++j) {
                    dp[i] = Math.min(dp[i], next[j] + nums[i]);
                }
            }
        }

        long minCost = INF;
        for (int i = n - k; i < n; ++i) {
            minCost = Math.min(minCost, dp[i]);
        }

        return minCost == INF ? -1 : minCost; // Return -1 if solution is impossible, though problem statement guarantees a solution
    }
}
