class Solution {

    /**
     * @param Integer[] $nums
     * @param Integer $k
     * @param Integer $dist
     * @return Integer
     */
    function minimumCost($nums, $k, $dist) {
        $n = count($nums);
        $dp = array_fill(0, $k + 1, array_fill(0, $n + 1, PHP_INT_MAX));
        $dp[0][0] = 0;

        for ($i = 1; $i <= $k; $i++) {
            for ($j = $i; $j <= $n; $j++) {
                for ($l = $i - 1; $l <= min($j - 1, $i - 1 + $dist); $l++) {
                    $dp[$i][$j] = min($dp[$i][$j], $dp[$i - 1][$l] + $nums[$j - 1]);
                }
            }
        }

        $result = PHP_INT_MAX;
        for ($i = $k - 1; $i <= min($n - 1, $k - 1 + $dist); $i++) {
            $result = min($result, $dp[$k][$i + 1]);
        }
        return $result;
    }
}
