class Solution {

    /**
     * @param Integer[] $nums
     * @param Integer $k
     * @param Integer $dist
     * @return Integer
     */
    function minimumCost($nums, $k, $dist) {
        $n = count($nums);
        $dp = array_fill(0, $k + 1, array_fill(0, $n, PHP_INT_MAX));
        $sum = 0;

        for ($i = 0; $i < $n; $i++) {
            if ($i < $k) {
                $sum += $nums[$i];
                $dp[$i + 1][$i] = $sum;
            }
            $dp[1][$i] = $nums[$i];
        }

        for ($i = 2; $i <= $k; $i++) {
            for ($j = $i - 1; $j < $n; $j++) {
                for ($l = max(0, $i - 2); $l <= $j - 1 && $j - $l <= $dist + 1; $l++) {
                    $dp[$i][$j] = min($dp[$i][$j], $dp[$i - 1][$l] + $nums[$j]);
                }
            }
        }

        $minCost = PHP_INT_MAX;
        for ($i = $k - 1; $i < $n; $i++) {
            $minCost = min($minCost, $dp[$k][$i]);
        }

        return $minCost;
    }
}
