class Solution(object):
    def minimumCost(self, nums, k, dist):
        """
        :type nums: List[int]
        :type k: int
        :type dist: int
        :rtype: int
        """
        from heapq import nsmallest

        def dp(i, k):
            if k == 1:
                return min(nums[i:])
            if (i, k) in memo:
                return memo[(i, k)]

            ans = float('inf')
            for j in range(i + 1, len(nums) - k + 2):
                if j - i <= dist + 1:
                    cost = nums[i] + dp(j, k - 1)
                    ans = min(ans, cost)
            memo[(i, k)] = ans
            return ans

        memo = {}
        return dp(0, k)
