from typing import List
from functools import lru_cache

class Solution:
    def minimumCost(self, nums: List[int], k: int, dist: int) -> int:
        n = len(nums)

        @lru_cache(None)
        def dfs(index, remaining_k):
            # Base case: if we are to select the last subarray
            if remaining_k == 1:
                return min(nums[index:])
            min_cost = float('inf')
            # Try every possible end for the next subarray within the distance limit
            for next_index in range(index + 1, min(n - remaining_k + 2, index + dist + 2)):
                cost = nums[index] + dfs(next_index, remaining_k - 1)
                min_cost = min(min_cost, cost)
            return min_cost

        return dfs(0, k)
