object Solution {
    def minimumCost(nums: Array[Int], k: Int, dist: Int): Long = {
        val n = nums.length
        val dp = Array.fill[Long](k, n)(Long.MaxValue)
        val prefixMin = Array.fill[Long](n)(0L)

        for (i <- 0 until n) {
            dp(0)(i) = nums(i)
            prefixMin(i) = if (i == 0) nums(i) else math.min(prefixMin(i - 1), nums(i).toLong)
        }

        for (i <- 1 until k) {
            for (j <- i until n) {
                var minCost = Long.MaxValue
                for (l <- math.max(0, j - dist) until j) {
                    minCost = math.min(minCost, dp(i - 1)(l))
                }
                dp(i)(j) = minCost + nums(j)
            }
        }

        var result = Long.MaxValue
        for (i <- k - 2 to n - 1) {
            result = math.min(result, dp(k - 1)(i))
        }

        result
    }
}
