object Solution {
    def minimumCost(nums: Array[Int], k: Int, dist: Int): Long = {
        val n = nums.length
        val dp = Array.fill(n + 1, k + 1)(Long.MaxValue)
        dp(0)(0) = 0

        for (i <- 1 to n) {
            for (j <- 1 to k) {
                var minIndex = i - 1
                while (minIndex >= 0 && i - minIndex <= dist + 1) {
                    dp(i)(j) = dp(i)(j) min (dp(minIndex)(j - 1) + nums(minIndex))
                    minIndex -= 1
                }
            }
        }

        var ans = Long.MaxValue
        for (i <- n - dist to n) {
            ans = ans min dp(i)(k)
        }
        ans
    }
}
