object Solution {
    def minimumCost(nums: Array[Int], k: Int, dist: Int): Long = {
        val n = nums.length
        val dp = Array.fill(n)(Array.fill(k)(Long.MaxValue))
        val sums = Array.fill(n)(0L)

        for (i <- 0 until n) {
            sums(i) = nums(i)
            if (i > 0) sums(i) += sums(i - 1)
        }

        def sum(i: Int, j: Int): Long = if (i == 0) sums(j) else sums(j) - sums(i - 1)

        for (i <- 0 until n) {
            dp(i)(0) = nums(i)
        }

        for (subarrayCount <- 1 until k) {
            for (end <- 0 until n) {
                for (start <- 0 to end if end - start <= dist) {
                    if (start > 0) {
                        dp(end)(subarrayCount) = Math.min(dp(end)(subarrayCount), dp(start - 1)(subarrayCount - 1) + nums(start))
                    } else if (subarrayCount == 1) {
                        dp(end)(subarrayCount) = Math.min(dp(end)(subarrayCount), nums(0))
                    }
                }
            }
        }

        var answer = Long.MaxValue
        for (i <- n - 1 until n) {
            answer = Math.min(answer, dp(i)(k - 1))
        }

        answer
    }
}
