function minimumCost(nums: number[], k: number, dist: number): number {
    const n = nums.length;
    const dp: number[][] = Array.from({ length: k + 1 }, () => Array(n).fill(Infinity));
    const prefixMin: number[][] = Array.from({ length: k + 1 }, () => Array(n).fill(Infinity));

    // Base case: cost of taking the first subarray ending at each position with only one subarray (k=1)
    for (let i = 0; i < n; i++) dp[1][i] = nums[0];

    for (let i = 2; i <= k; i++) {
        // Precompute prefix minimums for dp[i-1] to optimize inner loop
        prefixMin[i-1][0] = dp[i-1][0];
        for (let j = 1; j < n; j++) {
            prefixMin[i-1][j] = Math.min(prefixMin[i-1][j-1], dp[i-1][j]);
        }

        for (let j = i - 1; j < n; j++) {
            const validStart = Math.max(0, j - dist);
            if (validStart <= j) {
                dp[i][j] = prefixMin[i-1][j-1] + nums[j];
                if (validStart > 0) {
                    dp[i][j] = Math.min(dp[i][j], prefixMin[i-1][validStart - 1] + nums[j]);
                }
            }
        }
    }

    // Find minimum cost among all possible ending positions of the last subarray
    let result = Infinity;
    for (let i = k-1; i < n; i++) {
        result = Math.min(result, dp[k][i]);
    }

    return result;
}
