public class Solution {
    public IList<int> GoodSubsetofBinaryMatrix(int[][] grid) {
        int m = grid.Length, n = grid[0].Length;
        // Initialize the list to keep track of the best subset found
        List<int> bestSubset = new List<int>();

        // Use bit manipulation to generate all possible subsets
        for (int mask = 1; mask < (1 << m); mask++) {
            List<int> currentSubset = new List<int>();
            int[] columnSum = new int[n];
            int rowCount = 0;

            // Check each row if it's included in the current subset
            for (int i = 0; i < m; i++) {
                if ((mask & (1 << i)) != 0) {
                    currentSubset.Add(i);
                    rowCount++;
                    // Sum up the columns for the current subset
                    for (int j = 0; j < n; j++) {
                        columnSum[j] += grid[i][j];
                    }
                }
            }

            // Verify if the current subset is a good subset
            bool isGood = true;
            for (int j = 0; j < n; j++) {
                if (columnSum[j] > rowCount / 2) {
                    isGood = false;
                    break;
                }
            }

            // If a good subset is found and it's better than the previous one, update the bestSubset
            if (isGood && (bestSubset.Count == 0 || rowCount < bestSubset.Count)) {
                bestSubset.Clear();
                foreach (int index in currentSubset) {
                    bestSubset.Add(index);
                }
            }
        }

        // Return the best subset found, if any
        return bestSubset;
    }
}
