import java.util.ArrayList;
import java.util.List;

class Solution {
    public List<Integer> goodSubsetofBinaryMatrix(int[][] grid) {
        int m = grid.length, n = grid[0].length;
        List<Integer> result = new ArrayList<>();
        for (int mask = 1; mask < (1 << m); mask++) {
            List<Integer> subset = new ArrayList<>();
            boolean isValid = true;
            for (int col = 0; col < n; col++) {
                int sum = 0;
                for (int row = 0; row < m; row++) {
                    if ((mask & (1 << row)) != 0) {
                        sum += grid[row][col];
                        subset.add(row);
                    }
                }
                int subsetSize = Integer.bitCount(mask);
                if (sum > subsetSize / 2) {
                    isValid = false;
                    break;
                }
            }
            if (isValid) {
                // If the current subset is good and either no result is found yet
                // or the current subset is larger than the previous result, update the result.
                if (result.isEmpty() || subset.size() > result.size()) {
                    result = new ArrayList<>(subset);
                }
                // As we want just one good subset, we can return the first found subset
                break;
            }
            subset.clear();
        }
        // To ensure the result contains unique indices, create a set and then convert back to list
        return new ArrayList<>(new ArrayList<>(result));
    }
}
